<!DOCTYPE html><html <?php language_attributes()?>><head><link rel="profile" href="https://gmpg.org/xfn/11" /><meta http-equiv="Content-Type" content="<?php bloginfo('html_type')?>;charset=<?php bloginfo('charset') ?>" /><title><?= wp_get_document_title() ?></title><style media="screen" >body>footer,footer>aside{margin-bottom:2.75rem}body{font-family:system-ui,"Segoe UI",Roboto,Helvetica,Arial,sans-serif,"Apple Color Emoji","Segoe UI Emoji","Segoe UI Symbol";margin:1.75em auto;max-width:80vw;line-height:1.6;color:#444;padding:0 .75rem}main{display:flex;flex-direction:column;gap:20px}@media (min-width:768px){main{display:grid;gap:20px;grid-template-columns:3fr 1fr;grid-template-areas:"section aside";align-items:start}}section{display:grid;grid-row-gap:2em;grid-area:section;align-items:start}#sidebar{grid-area:aside}#sidebar>ul{list-style-type:none}#commentform>p:not(.comment-form-cookies-consent){display:flex;flex-direction:column}article{word-break:break-all;display:grid}body>footer{text-align:center;font-size:.75rem}img{max-width:100%;height:auto}h1,h2,h3,h4{line-height:1.2;margin:1.5rem 0 0;padding:0}</style><link rel="pingback" href="<?php bloginfo('pingback_url')?>"/><?php if (is_singular())wp_enqueue_script('comment-reply');wp_head()?></head><body <?php body_class()?>><header><h1><a href="<?= home_url()?>/"><?php bloginfo('name')?></a></h1><div class="description"><?php bloginfo('description')?></div></header><main id="page"><?php if(have_posts()){?><section><?php while(have_posts()){the_post()?><article><?php the_title('<h1>','</h1>');if(is_singular()){the_content();the_posts_navigation()?><address class="author"><?php _e('By: ','cg');the_author_link()?></address><?php _e('Published on: ','cg')?><time><?php the_date()?></time><?php if(comments_open()||get_comments_number()){?><footer class="comments"><?php if(post_password_required()){return;}?><h2><?php printf(_n('A comment on &ldquo;%2$s&rdquo;','%1$s comments on &ldquo;%2$s&rdquo;',get_comments_number(),'cg'),number_format_i18n(get_comments_number()),'<em>'.get_the_title().'</em>')?></h2><?php $cmms=get_comments(array('post_id'=>get_the_id()));wp_list_comments(['avatar_size'=>120,'style'=>'div','callback'=>function($comment,$args,$depth){?><aside<?php comment_class()?>id="comment-<?php comment_ID()?>"><?php if($comment->comment_approved=='0'){?><p><em><?php esc_html_e('Your comment is awaiting moderation.','cg') ?></em></p><?php }?><h3><time><?php __('on: ','cg').printf(esc_html__('%1$s at %2$s','cg'),get_comment_date(),get_comment_time())?></time><span><?= get_comment_author().__(' said:','cg')?></span></h3><p><?php comment_text()?></p><a href="#"><?php comment_reply_link(array_merge($args,array('depth'=>$depth,'max_depth'=>$args['max_depth']))) ?></a></aside><?php }],$cmms);the_comments_navigation();comment_form()?></footer><?php }}else{the_excerpt();echo '<a href="'.esc_url(get_permalink()).'">'. __('Read more','cg').'</a>';}?></article><?php }?></section><?php get_sidebar();}do_action('get_footer','',[]);wp_footer()?></main><footer id="footer" role="contentinfo"><p><?php printf(__('%1$s is proudly powered by %2$s'),get_bloginfo('name'),'<a href="https://wordpress.org/">WordPress</a>')?></p></footer></body></html><?php
